        await assert.rejects(endpoints.issue(require(
          './input/credential-status-missing-type-fail.json')));
        await assert.rejects(endpoints.issue(require(
          './input/credential-status-type-nonurl-fail.json')));
        await endpoints.issue(require(
          './input/credential-status-ok.json'));
      }

